ee:message.ee:set-payload:
%dw 2.0
output application/json
---
{
	name: "${mcp.server.name}",
	version: "${mcp.server.version}",
	description: "${mcp.server.description}",
	baseUrl: "${mcp.server.baseUrl}",
	environment: "${env}",
	capabilities: {
		databases: ["postgresql", "h2"],
		fallbackMode: "mock",
		multiTenant: false,
		audit: "${mcp.features.audit.enabled}",
		cache: "${cache.enabled}"
	},
	endpoints: {
		health: "/health",
		info: "/mcp/info",
		tools: "/mcp/tools"
	},
	tools: [
		{
			name: "allocate-assets",
			description: "Allocate assets to employees",
			endpoint: "/mcp/tools/allocate-assets",
			method: "POST",
			inputSchema: {
				employeeId: "string",
				assets: "array?",
				firstName: "string?",
				lastName: "string?",
				department: "string?",
				position: "string?"
			}
		},
		{
			name: "return-asset",
			description: "Return allocated assets",
			endpoint: "/mcp/tools/return-asset",
			method: "POST",
			inputSchema: {
				assetTag: "string",
				condition: "string?",
				notes: "string?"
			}
		},
		{
			name: "list-assets",
			description: "List all assets with status",
			endpoint: "/mcp/tools/list-assets",
			method: "GET",
			parameters: ["status?", "category?"]
		},
		{
			name: "get-available-assets",
			description: "Get available assets for allocation",
			endpoint: "/mcp/tools/get-available-assets",
			method: "GET"
		},
		{
			name: "get-employee-assets",
			description: "Get assets allocated to specific employee",
			endpoint: "/mcp/tools/get-employee-assets",
			method: "POST",
			inputSchema: {
				employeeId: "string"
			}
		}
	]
}

db:update.db:input-parameters:
#[{
				'assetTag': payload.assetTag,
				'condition': payload.condition default 'GOOD',
				'returnNotes': payload.notes default 'Asset returned via MCP'
			}]

ee:message.ee:set-payload:
%dw 2.0
output application/java
---
{
	name: "database",
	healthy: true,
	status: "UP",
	details: {
		"type": vars.dbType,
		assetCount: payload[0].asset_count,
		connection: "active"
	}
}

%dw 2.0
output application/java
---
{
	name: "database",
	healthy: false,
	status: "DOWN",
	details: {
		error: error.description,
		"type": "unavailable"
	}
